  `company_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '公司名称',
  `salt` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '密码盐',
  `type` int(1) UNSIGNED NULL DEFAULT 1 COMMENT '账号类型。1.首页注册，2.总后台添加的',
