  `mobile` varchar(11) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '手机号',
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '公司地址',
  `company_desc` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '公司介绍',
  `successions` int(10) UNSIGNED NULL DEFAULT 1 COMMENT '连续登录天数',
  `maxsuccessions` int(10) NULL DEFAULT NULL COMMENT '最大连续登录天数',
